import { useEffect, useState } from "react";
import Order from "../Order";
import axios from 'axios'


export default function Adopt(){
    const [allOrders, setAllOrders] = useState([])
    useEffect(()=>{
        async function fetchData(){
            const req= await axios.get('http://localhost:8080/adopt')
            setAllOrders(req.data)
            console.log(req.data)
        }
        fetchData()
    },[])

    return (<div className='container '> 
                <div className='row row-cols-3'>
                   {allOrders.map((order,index)=>{
                       (<div className="col mt-5" id={index}>
                           <Order   
                                title = {order.title}
                           />
                       </div>)
                   })}
                <div className="col mt-5">
                <Order
                    title='Se busca hogar para otto, es urgente por favorr as'
                    namePet ='otto'
                    age = '15'
                    address = 'Calle hernan cortez 180, Santa Patricia 4 etapa al costado del estadio monumnetal , preguntar por fresita'
                    city = 'Lima'
                    state = 'Lima'
                />
                 </div>
                
            
                </div>
            
            </div>)
}
